import { useState } from "react";

const cardExamples = [
  {
    id: 1,
    name: "Tomato",
    url: "data:image/jpeg;base64,",
    price: 1.5,
    Type: "VEGETABLE",
  },
  {
    id: 2,
    name: "Cucumber",
    url: "data:image/jpeg;base64,",
    price: 1.2,
    Type: "VEGETABLE",
  },
  {
    id: 3,
    name: "Apple",
    url: "data:image/jpeg;base64,",
    price: 2.5,
    Type: "FRUIT",
  },
  {
    id: 4,
    name: "Orange",
    url: "data:image/jpeg;base64,",
    price: 2.0,
    Type: "FRUIT",
  },
  {
    id: 5,
    name: "Banan",
    url: "data:image/jpeg;base64,",
    price: 1.0,
    Type: "FRUIT",
  },
];

const Card = ({ onIngredientAdd }) => {
  const [ingredients, setIngredients] = useState(cardExamples);

  const onAddIngredient = ({ ingredient }) => {
    setIngredients(ingredients.filter((i) => i.id !== ingredient.id));
    onIngredientAdd(ingredient);
  };
  return (
    <div className="pt-8">
      <h4 className="text-4xl">Dostępne składniki</h4>
      <div className="grid grid-cols-2 gap-4 p-12">
        {ingredients.map((ingredient) => (
          <div className="card bg-gray-700 w-96 shadow-sm" key={ingredient.id}>
            <figure>
              <img src={ingredient.url} alt="Shoes" />
            </figure>
            <div className="card-body w-full">
              <h2 className="card-title">{ingredient.name}</h2>
              <p>Składnik typu {ingredient.Type}</p>
              <p>Cena {ingredient.price} zł</p>
              <div className="card-actions justify-end">
                <button
                  className="btn btn-success"
                  onClick={() => onAddIngredient({ ingredient })}
                >
                  Dodaj
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default Card;
